import Main from './components/Main'
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FarmerWeather from './FarmerWeather';
import EventPlannerWeather from './EventPlannerWeather';
import TravelerWeather from './TravelerWeather';

function App() {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [userGroup, setUserGroup] = useState('general');

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=YOUR_API_KEY`);
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data', error);
    }
  };

  return (
    <div className="App">
      <div className="box">
        <div className="cityName">
          <h1>{city ? `${city}, ${weatherData?.sys?.country}` : 'Weather App'}</h1>
        </div>
        <div className="search">
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name"
          />
          <button onClick={handleSearch}>
            <img src="search-icon.png" alt="search" />
          </button>
        </div>
        <div className="userGroupButtons">
          <button onClick={() => setUserGroup('general')}>General</button>
          <button onClick={() => setUserGroup('farmer')}>Farmer</button>
          <button onClick={() => setUserGroup('event_planner')}>Event Planner</button>
          <button onClick={() => setUserGroup('traveler')}>Traveler</button>
        </div>
      </div>
      {weatherData && (
        <div className="weatherInfo">
          {userGroup === 'general' && (
            <>
              <h2>Current Weather</h2>
              <p>Temperature: {Math.round(weatherData.main.temp)}Â°C</p>
              <p>Humidity: {weatherData.main.humidity}%</p>
              <p>Rain: {weatherData.weather[0].description}</p>
              <p>Wind Speed: {weatherData.wind.speed} mph</p>
            </>
          )}
          {userGroup === 'farmer' && <FarmerWeather weatherData={weatherData.main} weather={weatherData.weather[0]} windData={weatherData.wind} />}
          {userGroup === 'event_planner' && <EventPlannerWeather weatherData={weatherData.main} weather={weatherData.weather[0]} windData={weatherData.wind} />}
          {userGroup === 'traveler' && <TravelerWeather weatherData={weatherData.main} weather={weatherData.weather[0]} windData={weatherData.wind} />}
        </div>
      )}
    </div>
  );
}

export default App;
