import React from 'react';


const EventPlannerWeather = ({ weatherData, weather, city, windData }) => (
    <div className='box'>
    <div className='cityName'>
      {error ? (
        <p>{cityDetails.name}, {cityDetails.country}
          <a href={`https://en.wikipedia.org/wiki/${cityDetails.name}`} target="_ ">
            <img src={linkIcon} alt='link'/>
          </a>
        </p>
      ) : (
        <p className='invalid'>{lang ? 'Invalid City Name' : 'अमान्य शहर का नाम'}</p>
      )}
      <div className='search'>
        <input type='text' ref={inputValue} onKeyDown={onkeydownHandler} placeholder='City Name'/>
        <img style={{cursor:'pointer'}} onClick={onSubmitHandler} src={searchIcon} alt='searchIcon'/>
      </div>
      <div>
      <button onClick={() => setUserGroup('general')}>General</button>
      <button onClick={() => setUserGroup('farmer')}>Farmer</button>
      <button onClick={() => setUserGroup('eventPlanner')}>Event Planner</button>
      <button onClick={() => setUserGroup('traveler')}>Traveler</button>
    </div>

    </div>

    
    {userGroup === 'general' && (
      <WeatherData weatherData={myData} weather={dataWeather} city={cityDetails} lang={lang} windData={windData} />
    )}
    {userGroup === 'farmer' && (
      <FarmerWeather weatherData={myData} weather={dataWeather} city={cityDetails} windData={windData} />
    )}
    {userGroup === 'eventPlanner' && (
      <EventPlannerWeather weatherData={myData} weather={dataWeather} city={cityDetails} windData={windData} />
    )}
    {userGroup === 'traveler' && (
      <TravelerWeather weatherData={myData} weather={dataWeather} city={cityDetails} windData={windData} />
    )}

    
    
  </div>
);

export default EventPlannerWeather;
