import React from 'react';

const EventPlannerWeather = ({ weatherData, weather, city, windData }) => {
  const suggestion = getSuggestions(weather, 'eventPlanner');

  return (
    <div className='weatherData'>
      <div className='currtemp'>
        <div className='tempAndLogo'>
          <div>
            <img src={`assets/${weather.icon}.svg`} width={200} alt='icon' />
          </div>
          <div>
            {Math.round(weatherData.temp)}&deg;C
            <p>{weather.description}</p>
          </div>
        </div>
        <div className='windData'>
          <p>Wind: <span>{windData.speed} mph</span></p>
          <p>Max Temp: <span>{Math.round(weatherData.temp_max)}&deg;C</span></p>
          <p>Min Temp: <span>{Math.round(weatherData.temp_min)}&deg;C</span></p>
        </div>
      </div>
      <div className='suggestion'>
        <h3>Suggestion:</h3>
        <p>{suggestion}</p>
      </div>
    </div>
  );
};

export default EventPlannerWeather;
