import React, { useEffect, useRef, useState, useCallback } from 'react';
import searchIcon from './search.svg';
import WeatherData from './WeatherData';
import linkIcon from './external-link.svg';

const Main = () => {
  const inputValue = useRef();
  const [cityName, setCityName] = useState("kakinada");
  const [error, setError] = useState(true);
  const [lang, setLang] = useState(true);
  const [weatherData, setWeatherData] = useState({});
  const [loading, setLoading] = useState(false);

  const APP_KEY = "540a03e69a8cd89d141951d056206b1d";

  useEffect(() => {
    const fetchWeatherData = async () => {
      setLoading(true);
      try {
        const response = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&APPID=${APP_KEY}&units=metric&lang=${lang ? 'en' : 'hi'}`);
        const data = await response.json();
        if (response.ok) {
          const { city, list } = data;
          const [firstEntry] = list;
          setWeatherData({
            city,
            main: firstEntry.main,
            weather: firstEntry.weather[0],
            wind: firstEntry.wind,
          });
          setError(false);
        } else {
          setError(true);
        }
      } catch (error) {
        setError(true);
      } finally {
        setLoading(false);
      }
    };
    fetchWeatherData();
  }, [cityName, lang]);

  const onkeydownHandler = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      setCityName(inputValue.current.value);
    }
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();
    setCityName(inputValue.current.value);
  };

  const toggleLang = () => setLang((prevLang) => !prevLang);

  return (
    <div className='box'>
      <div className='cityName'>
        {error ? (
          <p className='invalid'>{lang ? 'Invalid City Name' : 'अमान्य शहर का नाम'}</p>
        ) : (
          <p>
            {weatherData.city.name}, {weatherData.city.country}
            <a href={`https://en.wikipedia.org/wiki/${weatherData.city.name}`} target="_ ">
              <img src={linkIcon} alt='link' />
            </a>
          </p>
        )}
        <div className='search'>
          <input type='text' ref={inputValue} onKeyDown={onkeydownHandler} placeholder='City Name' />
          <img style={{ cursor: 'pointer' }} onClick={onSubmitHandler} src={searchIcon} alt='searchIcon' />
        </div>
      </div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <WeatherData weatherData={weatherData.main} weather={weatherData.weather} city={weatherData.city} lang={lang} windData={weatherData.wind} />
      )}
      <p onClick={toggleLang} className='translater'>
        {lang ? 'Hindi ?' : 'Eng ?'}
      </p>
    </div>
  );
};

export default Main;
